sys_script_include.script:
var activeUsers = Class.create();
activeUsers.prototype = {
    initialize: function() {
    },

	getActiveUser : function(){
		var gr = new GlideRecord('sys_user');
		gr.addActiveQuery();
		gr.query();

		while(gr.next()){
			return gr.user_name;
		}
	},

    type: 'activeUsers'
};
