sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    // Add your code here
    try {
        var r = new sn_ws.RESTMessageV2('x_1129749_to_do_0.Rest Messages 2', 'Update Product Dynamically');

        r.setStringParameterNoEscape('Description', current.product_description);
        r.setStringParameterNoEscape('ProductId', current.product_id);
		r.setStringParameterNoEscape('ProductCode', current.product_code);
        r.setStringParameterNoEscape('IsActive', current.active);
        r.setStringParameterNoEscape('Name', current.product_name);

        //override authentication profile 
        //authentication type ='basic'/ 'oauth2'
        //r.setAuthenticationProfile(authentication type, profile name);

        //set a MID server name if one wants to run the message on MID
        //r.setMIDServer('MY_MID_SERVER');

        //if the message is configured to communicate through ECC queue, either
        //by setting a MID server or calling executeAsync, one needs to set skip_sensor
        //to true. Otherwise, one may get an intermittent error that the response body is null
        //r.setEccParameter('skip_sensor', true);

        var response = r.execute();
        var responseBody = response.getBody();
        var httpStatus = response.getStatusCode();
		gs.info("Updated Successfully!");
		gs.addInfoMessage("Updated Successfully!");
		gs.info("Response : "+response);
		gs.info("Response Body : "+responseBody);
		gs.info("Http Status : "+httpStatus);
    } catch (ex) {
        var message = ex.message;
    }
})(current, previous);
